defmodule Instructions do
  def instructions([], _position), do: 0
  def instructions([?( | rest_of_path], position) do
    1 + instructions(rest_of_path, position+1)
  end
  def instructions([?) | rest_of_path], position) do
    current_floor = (-1 + instructions(rest_of_path, position+1))
    if current_floor < 0 do
      IO.puts "Santa went underground at position #{position}"
    end
    current_floor
  end

  def go_in_basement([], _floor, _position), do: "never goes in basement"
  def go_in_basement([?( | rest_of_path], floor, position) do
    go_in_basement(rest_of_path, floor+1, position+1)
  end
  def go_in_basement([?) | rest_of_path], floor, position) when floor == 0 do
    position
  end
  def go_in_basement([?) | rest_of_path], floor, position) do
    go_in_basement(rest_of_path, floor-1, position+1)
  end
end

